              </div>
              <ColumnVisibilityDropdown
                columns={SALES_COLUMNS}
                visibleColumns={visibleColumns}
                onVisibilityChange={handleVisibilityChange}
                onHideAll={handleHideAll}
                onResetToDefault={handleResetToDefault}
              />
            </div>
          </div>

          {/* Sales Table */}
          <SalesTable 
            sales={filteredSales} 
            isLoading={isLoading}
            visibleColumns={visibleColumns}
          />
        </div>
      </main>
    </>
  );
}

