import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";
import { Separator } from "@/components/ui/separator";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Eye, Search } from "lucide-react";

export interface ColumnDefinition {
  key: string;
  label: string;
  defaultVisible: boolean;
}

interface ColumnVisibilityDropdownProps {
  columns: ColumnDefinition[];
  visibleColumns: Record<string, boolean>;
  onVisibilityChange: (key: string, visible: boolean) => void;
  onHideAll: () => void;
  onResetToDefault: () => void;
}

export default function ColumnVisibilityDropdown({
  columns,
  visibleColumns,
  onVisibilityChange,
  onHideAll,
  onResetToDefault,
}: ColumnVisibilityDropdownProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [isOpen, setIsOpen] = useState(false);

  const filteredColumns = columns.filter((column) =>
    column.label.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const visibleCount = Object.values(visibleColumns).filter(Boolean).length;

  return (
    <DropdownMenu open={isOpen} onOpenChange={setIsOpen}>
      <DropdownMenuTrigger asChild>
        <Button
          variant="outline"
          size="sm"
          className="flex items-center space-x-2"
          data-testid="button-columns"
        >
          <Eye size={16} />
          <span>Columns ({visibleCount})</span>
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent
        align="end"
        className="w-64 p-0"
        data-testid="dropdown-columns"
      >
        <Card className="border-0 shadow-none">
          <div className="p-4 space-y-4">
            {/* Search Bar */}
            <div className="relative">
              <Input
                type="text"
                placeholder="Search columns..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-9"
                data-testid="input-search-columns"
              />
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={14} />
            </div>

            {/* Preset Actions */}
            <div className="space-y-2">
              <Button
                variant="outline"
                size="sm"
                onClick={onHideAll}
                className="w-full justify-start text-sm"
                data-testid="button-hide-all"
              >
                Hide All
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={onResetToDefault}
                className="w-full justify-start text-sm"
                data-testid="button-reset-columns"
              >
                Reset to Default
              </Button>
            </div>

            <Separator />

            {/* Column List */}
            <div className="space-y-2 max-h-64 overflow-y-auto">
              {filteredColumns.map((column) => (
                <div
                  key={column.key}
                  className="flex items-center space-x-2 py-1"
                  data-testid={`column-option-${column.key}`}
                >
                  <Checkbox
                    id={`column-${column.key}`}
                    checked={visibleColumns[column.key] || false}
                    onCheckedChange={(checked) =>
                      onVisibilityChange(column.key, !!checked)
                    }
                    data-testid={`checkbox-column-${column.key}`}
                  />
                  <label
                    htmlFor={`column-${column.key}`}
                    className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70 cursor-pointer flex-1"
                  >
                    {column.label}
                  </label>
                </div>
              ))}
              {filteredColumns.length === 0 && (
                <p className="text-sm text-gray-500 text-center py-2">
                  No columns match "{searchQuery}"
                </p>
              )}
            </div>
          </div>
        </Card>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

server/middleware/auth.ts.content:
import { Request, Response, NextFunction } from "express";
import jwt from "jsonwebtoken";
import bcrypt from "bcryptjs";
import { type JWTPayload, type UserType } from "@shared/schema";
import { env } from "../config/env";
import { UnauthorizedError, ForbiddenError } from "../utils/errors";

// Extend Express Request type to include user
declare global {
  namespace Express {
    interface Request {
      user?: JWTPayload;
    }
  }
}

export interface AuthenticatedRequest extends Request {
  user: JWTPayload;
}

// JWT Authentication Middleware
export const authenticateToken = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  try {
    const authHeader = req.headers.authorization;
    const token = authHeader && authHeader.split(' ')[1]; // Bearer TOKEN

    if (!token) {
      throw new UnauthorizedError("Access token required");
    }

    const decoded = jwt.verify(token, env.JWT_SECRET) as JWTPayload;
    req.user = decoded;
    next();
  } catch (error) {
    if (error instanceof jwt.JsonWebTokenError) {
      next(new UnauthorizedError("Invalid token"));
    } else if (error instanceof jwt.TokenExpiredError) {
      next(new UnauthorizedError("Token expired"));
    } else {
      next(error);
    }
  }
};

// Role-based Authorization Middleware
export const authorizeRoles = (...allowedRoles: UserType[]) => {
  return (req: Request, res: Response, next: NextFunction): void => {
    if (!req.user) {
      next(new UnauthorizedError("Authentication required"));
      return;
    }

    if (!allowedRoles.includes(req.user.userType)) {
      next(new ForbiddenError(`Access denied. Required roles: ${allowedRoles.join(", ")}`));
      return;
    }

    next();
  };
};

// Specific role authorization helpers
export const requireAdmin = authorizeRoles("admin");
export const requireManagerOrAdmin = authorizeRoles("admin", "manager");
export const requireAnyRole = authorizeRoles("admin", "manager", "employee");

// Utility function to generate JWT token
export const generateToken = (payload: JWTPayload): string => {
  return jwt.sign(payload, env.JWT_SECRET, { 
    expiresIn: env.JWT_EXPIRES_IN
  } as jwt.SignOptions);
};

// Utility function to hash password
export const hashPassword = async (password: string): Promise<string> => {
  const saltRounds = 12;
  return bcrypt.hash(password, saltRounds);
};

// Utility function to compare password
export const comparePassword = async (
  plainPassword: string, 
  hashedPassword: string
): Promise<boolean> => {
  return bcrypt.compare(plainPassword, hashedPassword);
};

