import { Router } from "express";
import userRoutes from "./userRoutes";
import inventoryRoutes from "./inventoryRoutes";
import salesRoutes from "./salesRoutes";
import settingsRoutes from "./settingsRoutes";

const router = Router();

// Mount route modules
router.use("/auth", userRoutes);
router.use("/users", userRoutes); // For admin user management
router.use("/inventory", inventoryRoutes);
router.use("/sales", salesRoutes);
router.use("/settings", settingsRoutes);

// Add a compatibility route for stats that was in the old routes
router.get("/stats", async (req, res, next) => {
  try {
    // Import the getSalesStats controller dynamically
    const { getSalesStats } = await import("../controllers");
    const { authenticateToken, requireAnyRole } = await import("../middleware");
    // Apply middleware manually
    await new Promise<void>((resolve, reject) => {
      authenticateToken(req, res, (err) => {
        if (err) reject(err);
        else resolve();
      });
    });
    await new Promise<void>((resolve, reject) => {
      requireAnyRole(req, res, (err) => {
        if (err) reject(err);
        else resolve();
      });
    });
    return getSalesStats(req, res, next);
  } catch (error) {
    next(error);
  }
});

export default router;

server/routes/inventoryRoutes.ts.content:
import { Router } from "express";
import {
  getAllInventory,
  getInventoryById,
  getInventoryByVin,
  getInventoryByStockNumber,
  createInventoryItem,
  updateInventoryItem,
  deleteInventoryItem,
  searchInventory
} from "../controllers";
import { 
  authenticateToken, 
  requireAdmin, 
  requireManagerOrAdmin,
  requireAnyRole,
  validateBody
} from "../middleware";
import { insertInventorySchema } from "@shared/schema";

const router = Router();

// All routes require authentication
router.use(authenticateToken);

// View routes (all roles)
router.get("/", requireAnyRole, getAllInventory);
router.get("/search/:query", requireAnyRole, searchInventory);
router.get("/vin/:vin", requireAnyRole, getInventoryByVin);
router.get("/stock/:stockNumber", requireAnyRole, getInventoryByStockNumber);
router.get("/:id", requireAnyRole, getInventoryById);

// Create/Update routes (managers and admins)
router.post("/", requireManagerOrAdmin, validateBody(insertInventorySchema), createInventoryItem);
router.put("/:id", requireManagerOrAdmin, updateInventoryItem);

// Delete routes (admin only)
router.delete("/:id", requireAdmin, deleteInventoryItem);

export default router;

